actions.script:
/* 自定义JS使用说明：
  * 1.动作执行函数doAction，可以执行所有类型的动作
  * 2.通过上下文对象context可以获取当前组件实例，例如context.props可以获取该组件相关属性
  * 3.事件对象event，在doAction之后执行event.stopPropagation();可以阻止后续动作执行
*/
let body = {};
let params = []
console.log('method:valuechange:event.data.selectedItems')
console.log(event.data.selectedItems)
if (event.data.selectedItems && event.data.selectedItems.configJson) {
  var configJson = JSON.parse(event.data.selectedItems.configJson);
  console.log('method:event.data.selectedItems.configJson')
  console.log(configJson)
  params = configJson.params;
  if (params) {
    params.forEach(p => {
      let anno = p.typeAnnotation;
      let parValue = null;
      if (anno.typeKind == 'reference' && anno.properties && anno.properties.length > 0) {
        parValue = {};
        anno.properties.forEach(prop => {
          parValue[prop.name] = JSON.parse(prop.defaultValue);
        })
      }
      body[p.name] = parValue;
    })
  } else {
    body = {}
  }

}
doAction({
  actionType: 'setValue',
  componentId: 'item_body',
  args: {
    value: `return ` + JSON.stringify(body, null, 2)
  }
});
